import React, { useState } from 'react';

interface SendToGitHubButtonProps {
  phaseId: string | number;
  className?: string;
}

type Status = 'idle' | 'loading' | 'success' | 'error';

export const SendToGitHubButton: React.FC<SendToGitHubButtonProps> = ({ 
  phaseId, 
  className = '' 
}) => {
  const [status, setStatus] = useState<Status>('idle');
  const [errorMessage, setErrorMessage] = useState<string>('');

  const handleClick = async () => {
    setStatus('loading');
    setErrorMessage('');

    try {
      const response = await fetch('/api/github/trigger', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ phase_id: String(phaseId) }),
      });

      const data = await response.json();

      if (response.ok) {
        setStatus('success');
        // Reset to idle after 3 seconds
        setTimeout(() => setStatus('idle'), 3000);
      } else {
        setStatus('error');
        setErrorMessage(data.error || 'Failed to trigger workflow');
      }
    } catch (_error) { // no-unused-vars fix
      setStatus('error');
      setErrorMessage('Network error: Unable to connect');
    }
  };

  const getButtonContent = () => {
    switch (status) {
      case 'loading':
        return (
          <>
            <span className="animate-spin inline-block w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2" />
            Sending...
          </>
        );
      case 'success':
        return (
          <>
            <svg className="w-4 h-4 mr-2 inline-block" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 111.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
            </svg>
            Sent to GitHub
          </>
        );
      case 'error':
        return (
          <>
            <svg className="w-4 h-4 mr-2 inline-block" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
            Failed
          </>
        );
      default:
        return (
          <>
            <svg className="w-4 h-4 mr-2 inline-block" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
            </svg>
            Send to GitHub
          </>
        );
    }
  };

  const getButtonClasses = () => {
    const baseClasses = 'inline-flex items-center px-4 py-2 rounded-md font-medium transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2';

    switch (status) {
      case 'loading':
        return `${baseClasses} bg-gray-400 text-white cursor-not-allowed`;
      case 'success':
        return `${baseClasses} bg-green-600 text-white`;
      case 'error':
        return `${baseClasses} bg-red-600 text-white`;
      default:
        return `${baseClasses} bg-blue-600 text-white hover:bg-blue-700 focus:ring-blue-500`;
    }
  };

  return (
    <div className="inline-flex flex-col items-start">
      <button
        onClick={handleClick}
        disabled={status === 'loading'}
        className={`${getButtonClasses()} ${className}`}
        aria-label={`Send phase ${phaseId} to GitHub`}
        data-testid="send-to-github-button"
      >
        {getButtonContent()}
      </button>
      {status === 'error' && errorMessage && (
        <span className="text-sm text-red-600 mt-1" data-testid="error-message">{errorMessage}</span>
      )}
      {status === 'success' && (
        <span className="text-sm text-green-600 mt-1" data-testid="success-message">Workflow triggered successfully</span>
      )}
    </div>
  );
};

import React, { useState, useCallback } from 'react'; // no-unused-vars fix
import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import Placeholder from '@tiptap/extension-placeholder';
import Typography from '@tiptap/extension-typography';
import { 
  Bold, Italic, Code, Heading1, Heading2, 
  List, Quote, Save, Download, Sparkles, Clock,
  Check, AlertCircle, Loader2, Tag, Link2, FileText
} from 'lucide-react';
import type { DocsEntry, DocType, SaveStatus } from '../../types/docs';
import { useProjectContext } from '../../contexts/ProjectContext';
import { AIPromptModal } from './AIPromptModal';

interface DocsEditorProps {
  initialDoc?: DocsEntry;
  onSave?: (doc: DocsEntry) => Promise<void>;
  onExport?: (format: 'markdown' | 'drive' | 'memory', content: string) => Promise<void>;
}

export const DocsEditor: React.FC<DocsEditorProps> = ({ 
  initialDoc, 
  onSave,
  onExport 
}) => {
  const { projects, logGovernanceEvent } = useProjectContext();

  // Metadata state
  const [title, setTitle] = useState(initialDoc?.title || '');
  const [docType, setDocType] = useState<DocType>(initialDoc?.docType || 'SOP');
  const [_relatedFeatureId] = useState(initialDoc?.relatedFeatureId || ''); // setter unused
  const [relatedPhaseId, setRelatedPhaseId] = useState(initialDoc?.relatedPhaseId || '');
  const [relatedProjectId, setRelatedProjectId] = useState(initialDoc?.relatedProjectId || '');
  const [tags, setTags] = useState<string[]>(initialDoc?.tags || []);
  const [tagInput, setTagInput] = useState('');

  // Editor state
  const [saveStatus, setSaveStatus] = useState<SaveStatus>('saved');
  const [lastSaved, setLastSaved] = useState<Date | null>(null);
  const [showAIModal, setShowAIModal] = useState(false);
  const [showExportMenu, setShowExportMenu] = useState(false);

  // Initialize Tiptap editor
  const editor = useEditor({
    extensions: [
      StarterKit.configure({
        heading: {
          levels: [1, 2, 3]
        }
      }),
      Placeholder.configure({
        placeholder: 'Start writing your document...',
        emptyEditorClass: 'is-editor-empty',
      }),
      Typography
    ],
    content: initialDoc?.content || '<p></p>',
    onUpdate: ({ editor }) => {
      setSaveStatus('unsaved');
      // Trigger autosave after 2 seconds of inactivity
      clearTimeout(window.autoSaveTimeout);
      window.autoSaveTimeout = setTimeout(() => {
        handleAutoSave();
      }, 2000);
    },
    editorProps: {
      attributes: {
        class: 'prose prose-sm max-w-none focus:outline-none min-h-[400px] p-4'
      }
    }
  });

  // Autosave handler
  const handleAutoSave = useCallback(async () => {
    if (!editor || saveStatus === 'saving') return;

    setSaveStatus('saving');

    const doc: DocsEntry = {
      id: initialDoc?.id || `doc-${Date.now()}`,
      title,
      docType,
      content: editor.getHTML(),
      relatedFeatureId: _relatedFeatureId || undefined,
      relatedPhaseId: relatedPhaseId || undefined,
      relatedProjectId: relatedProjectId || undefined,
      memoryAnchorId: initialDoc?.memoryAnchorId,
      driveLink: initialDoc?.driveLink,
      tags: tags.length > 0 ? tags : undefined,
      lastUpdated: new Date().toISOString(),
      createdBy: 'current-user' // In real app, get from auth context
    };

    try {
      if (onSave) {
        await onSave(doc);
      }
      setSaveStatus('saved');
      setLastSaved(new Date());

      // Log to governance
      logGovernanceEvent({
        phaseStepId: doc.id,
        newStatus: 'complete',
        triggeredBy: 'current-user',
        eventType: 'StepStatusUpdated',
        details: {
          action: 'document_saved',
          docType: doc.docType,
          title: doc.title
        }
      });
    } catch (error) {
      setSaveStatus('failed');
      console.error('Failed to save document:', error);
    }
  }, [editor, title, docType, _relatedFeatureId, relatedPhaseId, relatedProjectId, tags, initialDoc, onSave, logGovernanceEvent, saveStatus]);

  // Toolbar actions
  const toolbarActions = [
    { 
      icon: Bold, 
      action: () => editor?.chain().focus().toggleBold().run(),
      isActive: () => editor?.isActive('bold'),
      tooltip: 'Bold'
    },
    { 
      icon: Italic, 
      action: () => editor?.chain().focus().toggleItalic().run(),
      isActive: () => editor?.isActive('italic'),
      tooltip: 'Italic'
    },
    { 
      icon: Code, 
      action: () => editor?.chain().focus().toggleCode().run(),
      isActive: () => editor?.isActive('code'),
      tooltip: 'Code'
    },
    { 
      icon: Heading1, 
      action: () => editor?.chain().focus().toggleHeading({ level: 1 }).run(),
      isActive: () => editor?.isActive('heading', { level: 1 }),
      tooltip: 'Heading 1'
    },
    { 
      icon: Heading2, 
      action: () => editor?.chain().focus().toggleHeading({ level: 2 }).run(),
      isActive: () => editor?.isActive('heading', { level: 2 }),
      tooltip: 'Heading 2'
    },
    { 
      icon: List, 
      action: () => editor?.chain().focus().toggleBulletList().run(),
      isActive: () => editor?.isActive('bulletList'),
      tooltip: 'Bullet List'
    },
    { 
      icon: Quote, 
      action: () => editor?.chain().focus().toggleBlockquote().run(),
      isActive: () => editor?.isActive('blockquote'),
      tooltip: 'Quote'
    }
  ];

  const handleExport = async (format: 'markdown' | 'drive' | 'memory') => {
    if (!editor) return;

    const content = editor.getHTML();

    try {
      if (onExport) {
        await onExport(format, content);
      }

      // Log export action
      logGovernanceEvent({
        phaseStepId: initialDoc?.id || 'new-doc',
        newStatus: 'complete',
        triggeredBy: 'current-user',
        eventType: 'StepStatusUpdated',
        details: {
          action: 'document_exported',
          format,
          title
        }
      });

      setShowExportMenu(false);
    } catch (error) {
      console.error(`Failed to export as ${format}:`, error);
    }
  };

  const handleAddTag = () => {
    if (tagInput.trim() && !tags.includes(tagInput.trim())) {
      setTags([...tags, tagInput.trim()]);
      setTagInput('');
      setSaveStatus('unsaved');
    }
  };

  const handleRemoveTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
    setSaveStatus('unsaved');
  };

  // Get related items for dropdowns
  const allPhases = projects.flatMap(p => p.phases.map(phase => ({ 
    id: phase.id, 
    name: phase.name, 
    projectName: p.name 
  })));

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200">
      {/* Header with title and status */}
      <div className="border-b border-gray-200 p-4">
        <div className="flex items-start justify-between gap-4">
          <div className="flex-1">
            <input
              type="text"
              value={title}
              onChange={(e) => {
                setTitle(e.target.value);
                setSaveStatus('unsaved');
              }}
              placeholder="Document Title"
              className="w-full text-2xl font-bold text-gray-900 border-none outline-none focus:ring-0 placeholder-gray-400"
            />
          </div>

          <div className="flex items-center gap-2">
            {/* Save status indicator */}
            <div className="flex items-center gap-2 text-sm">
              {saveStatus === 'saved' && (
                <>
                  <Check className="w-4 h-4 text-green-600" />
                  <span className="text-gray-600">Saved</span>
                </>
              )}
              {saveStatus === 'saving' && (
                <>
                  <Loader2 className="w-4 h-4 text-blue-600 animate-spin" />
                  <span className="text-gray-600">Saving...</span>
                </>
              )}
              {saveStatus === 'failed' && (
                <>
                  <AlertCircle className="w-4 h-4 text-red-600" />
                  <span className="text-red-600">Failed</span>
                </>
              )}
              {saveStatus === 'unsaved' && (
                <>
                  <Clock className="w-4 h-4 text-amber-600" />
                  <span className="text-amber-600">Unsaved</span>
                </>
              )}
            </div>

            {lastSaved && (
              <span className="text-xs text-gray-500">
                Last saved: {lastSaved.toLocaleTimeString()}
              </span>
            )}
          </div>
        </div>
      </div>

      {/* Metadata fields */}
      <div className="border-b border-gray-200 p-4 bg-gray-50">
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Document Type
            </label>
            <select
              value={docType}
              onChange={(e) => {
                setDocType(e.target.value as DocType);
                setSaveStatus('unsaved');
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="SOP">SOP</option>
              <option value="ProjectSpec">Project Spec</option>
              <option value="Template">Template</option>
              <option value="Checklist">Checklist</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Related Project
            </label>
            <select
              value={relatedProjectId}
              onChange={(e) => {
                setRelatedProjectId(e.target.value);
                setSaveStatus('unsaved');
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">None</option>
              {projects.map(project => (
                <option key={project.id} value={project.id}>
                  {project.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Related Phase
            </label>
            <select
              value={relatedPhaseId}
              onChange={(e) => {
                setRelatedPhaseId(e.target.value);
                setSaveStatus('unsaved');
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">None</option>
              {allPhases.map(phase => (
                <option key={phase.id} value={phase.id}>
                  {phase.name} ({phase.projectName})
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Tags
            </label>
            <div className="flex items-center gap-2">
              <input
                type="text"
                value={tagInput}
                onChange={(e) => setTagInput(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    e.preventDefault();
                    handleAddTag();
                  }
                }}
                placeholder="Add tag..."
                className="flex-1 px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button
                onClick={handleAddTag}
                className="p-2 text-gray-600 hover:text-gray-900"
              >
                <Tag className="w-4 h-4" />
              </button>
            </div>
          </div>
        </div>

        {/* Tags display */}
        {tags.length > 0 && (
          <div className="flex flex-wrap gap-2 mt-3">
            {tags.map(tag => (
              <span
                key={tag}
                className="inline-flex items-center gap-1 px-2 py-1 bg-blue-100 text-blue-700 rounded-md text-sm"
              >
                {tag}
                <button
                  onClick={() => handleRemoveTag(tag)}
                  className="hover:text-blue-900"
                >
                  √ó
                </button>
              </span>
            ))}
          </div>
        )}

        {/* Memory and Drive links */}
        {(initialDoc?.memoryAnchorId || initialDoc?.driveLink) && (
          <div className="flex items-center gap-4 mt-3 text-sm text-gray-600">
            {initialDoc.memoryAnchorId && (
              <div className="flex items-center gap-1">
                <Link2 className="w-4 h-4" />
                <span>Memory: {initialDoc.memoryAnchorId}</span>
              </div>
            )}
            {initialDoc.driveLink && (
              <div className="flex items-center gap-1">
                <FileText className="w-4 h-4" />
                <a href={initialDoc.driveLink} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">
                  Drive Link
                </a>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Toolbar */}
      <div className="border-b border-gray-200 p-2 flex items-center justify-between">
        <div className="flex items-center gap-1">
          {toolbarActions.map((action, index) => {
            const Icon = action.icon;
            return (
              <button
                key={index}
                onClick={action.action}
                className={`p-2 rounded hover:bg-gray-100 transition-colors ${
                  action.isActive?.() ? 'bg-gray-200 text-blue-600' : 'text-gray-600'
                }`}
                title={action.tooltip}
              >
                <Icon className="w-4 h-4" />
              </button>
            );
          })}
        </div>

        <div className="flex items-center gap-2">
          <button
            onClick={() => setShowAIModal(true)}
            className="inline-flex items-center gap-2 px-3 py-1.5 bg-purple-600 text-white rounded-md hover:bg-purple-700 transition-colors text-sm"
          >
            <Sparkles className="w-4 h-4" />
            Ask AI
          </button>

          <div className="relative">
            <button
              onClick={() => setShowExportMenu(!showExportMenu)}
              className="inline-flex items-center gap-2 px-3 py-1.5 bg-gray-600 text-white rounded-md hover:bg-gray-700 transition-colors text-sm"
            >
              <Download className="w-4 h-4" />
              Export
            </button>

            {showExportMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg border border-gray-200 z-10">
                <button
                  onClick={() => handleExport('markdown')}
                  className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                >
                  Markdown (.md)
                </button>
                <button
                  onClick={() => handleExport('drive')}
                  className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                >
                  Google Drive
                </button>
                <button
                  onClick={() => handleExport('memory')}
                  className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                >
                  MemoryPlugin
                </button>
              </div>
            )}
          </div>

          <button
            onClick={handleAutoSave}
            className="inline-flex items-center gap-2 px-3 py-1.5 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors text-sm"
          >
            <Save className="w-4 h-4" />
            Save & Log
          </button>
        </div>
      </div>

      {/* Editor */}
      <div className="min-h-[400px]">
        <EditorContent editor={editor} />
      </div>

      {/* AI Modal */}
      <AIPromptModal
        isOpen={showAIModal}
        onClose={() => setShowAIModal(false)}
        onApplyResult={(content, action) => {
          if (!editor) return;

          if (action === 'replace') {
            editor.commands.setContent(content);
          } else if (action === 'append') {
            const currentContent = editor.getHTML();
            editor.commands.setContent(currentContent + '\n\n' + content);
          } else if (action === 'insert') {
            editor.commands.insertContent(content);
          }

          setSaveStatus('unsaved');
        }}
        currentContent={editor?.getHTML() || ''}
        documentTitle={title}
        docType={docType}
      />
    </div>
  );
};

// Extend window type for autosave timeout
declare global {
  interface Window {
    autoSaveTimeout: NodeJS.Timeout;
  }
}

import React, { useState, useMemo } from 'react';
import { ChevronLeft, ChevronRight, Filter, FolderOpen, Settings, Plus } from 'lucide-react'; // removed unused User
import type { Project } from '../../types/phase';
import type { WorkSurface } from './AppLayout';
import { SubAppSelector } from './SubAppSelector';
import { mockPrograms } from '../../data/mockPrograms';
import { mockProjects } from '../../data/mockProjects';

interface EnhancedProjectSidebarProps {
  projects: Project[];
  currentProject: Project;
  selectedSurface: WorkSurface;
  collapsed: boolean;
  currentSubApp: string;
  onProjectChange: (project: Project) => void;
  onSurfaceChange: (surface: WorkSurface) => void;
  onToggleCollapse: () => void;
  onSubAppChange: (subAppId: string) => void;
}

interface FilterState {
  ragStatus: string;
  status: string;
  owner: string;
}

const WORK_SURFACES: { id: WorkSurface; label: string; icon: string; description: string }[] = [
  { id: 'plan', label: 'Plan', icon: 'üìã', description: 'Composer, phase setup, AI scaffold' },
  { id: 'execute', label: 'Execute', icon: '‚ö°', description: 'Track phases, trigger steps, flag blockers' },
  { id: 'document', label: 'Document', icon: 'üìù', description: 'Rich-text SOP + AI' },
  { id: 'govern', label: 'Govern', icon: 'üõ°Ô∏è', description: 'Logs, reviews, AI audit trails' },
  { id: 'integrate', label: 'Integrate', icon: 'üß¨', description: 'Integration health monitoring' }
];

const RAG_STATUS_OPTIONS = ['All', 'Red', 'Amber', 'Green'];
const STATUS_OPTIONS = ['All', 'Active', 'On Hold', 'Completed'];

export const EnhancedProjectSidebar: React.FC<EnhancedProjectSidebarProps> = ({
  _projects, // unused in this component
  currentProject,
  selectedSurface,
  collapsed,
  currentSubApp,
  onProjectChange,
  onSurfaceChange,
  onToggleCollapse,
  onSubAppChange
}) => {
  const [showProjectSelector, setShowProjectSelector] = useState(false);
  const [filters, setFilters] = useState<FilterState>({
    ragStatus: 'All',
    status: 'All',
    owner: 'All'
  });

  // Filter projects by current Sub-App
  const filteredProjects = useMemo(() => {
    // Get mock projects for demo purposes, filtered by linkedProgramId
    const subAppProjects = mockProjects.filter(p => p.linkedProgramId === currentSubApp);

    // Map to match expected Project type structure
    return subAppProjects.map(p => ({
      id: p.id,
      name: p.title,
      projectType: 'Development',
      projectOwner: 'Team Lead',
      currentPhase: 'Phase 1',
      completionPercentage: Math.floor(Math.random() * 100),
      phases: []
    })) as Project[];
  }, [currentSubApp]);

  const currentProgram = mockPrograms.find(p => p.id === currentSubApp);
  const uniqueOwners = ['All', ...new Set(filteredProjects.map(p => p.projectOwner))];

  // Project status color utility removed - unused in this component

  const getProjectRAGStatus = (project: Project) => {
    const percentage = project.completionPercentage || 0;
    if (percentage >= 80) return 'Green';
    if (percentage >= 50) return 'Amber';
    return 'Red';
  };

  if (collapsed) {
    return (
      <div className="fixed left-0 top-0 h-full w-16 bg-white border-r border-gray-200 shadow-sm z-40">
        <div className="p-4">
          <button
            onClick={onToggleCollapse}
            className="w-8 h-8 flex items-center justify-center text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-md transition-colors"
            data-testid="sidebar-expand"
          >
            <ChevronRight className="w-4 h-4" />
          </button>
        </div>

        <nav className="mt-8 space-y-2 px-2">
          {WORK_SURFACES.map((surface) => (
            <button
              key={surface.id}
              onClick={() => onSurfaceChange(surface.id)}
              className={`w-12 h-12 flex items-center justify-center rounded-lg transition-colors ${
                selectedSurface === surface.id
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
              title={surface.label}
              data-testid={`surface-${surface.id}-collapsed`}
            >
              <span className="text-lg">{surface.icon}</span>
            </button>
          ))}
        </nav>
      </div>
    );
  }

  return (
    <div className="fixed left-0 top-0 h-full w-80 bg-white border-r border-gray-200 shadow-sm z-40 flex flex-col">
      {/* Header with Platform Title */}
      <div className="p-6 pb-0 border-b border-gray-200">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-xl font-bold text-gray-900">Orbis Platform</h1>
          <button
            onClick={onToggleCollapse}
            className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-md transition-colors"
            data-testid="sidebar-collapse"
          >
            <ChevronLeft className="w-4 h-4" />
          </button>
        </div>
      </div>

      {/* Sub-App Selector */}
      <SubAppSelector
        currentSubApp={currentSubApp}
        onSubAppChange={onSubAppChange}
        availableSubApps={mockPrograms}
        showBranding={true}
      />

      {/* Project Selector (Scoped to Sub-App) */}
      <div className="p-6 pt-4 border-b border-gray-200">
        <div className="flex items-center justify-between mb-2">
          <h3 className="text-sm font-medium text-gray-700">
            {currentProgram?.name} Projects
          </h3>
          <button className="p-1 text-gray-400 hover:text-gray-600">
            <Plus className="w-4 h-4" />
          </button>
        </div>

        <div className="relative">
          <button
            onClick={() => setShowProjectSelector(!showProjectSelector)}
            className="w-full flex items-center justify-between p-3 bg-gray-50 hover:bg-gray-100 rounded-lg transition-colors"
            data-testid="project-selector-trigger"
          >
            <div className="flex items-center space-x-3">
              <FolderOpen className="w-5 h-5 text-gray-500" />
              <div className="text-left">
                <div className="font-medium text-gray-900 truncate">{currentProject.name}</div>
                <div className="text-sm text-gray-500">{currentProject.projectType}</div>
              </div>
            </div>
            <ChevronRight className={`w-4 h-4 text-gray-400 transition-transform ${
              showProjectSelector ? 'rotate-90' : ''
            }`} />
          </button>

          {showProjectSelector && (
            <div className="absolute top-full left-0 right-0 mt-2 bg-white border border-gray-200 rounded-lg shadow-lg z-50 max-h-64 overflow-y-auto">
              <div className="p-2">
                {filteredProjects.length > 0 ? (
                  filteredProjects.map((project) => (
                    <button
                      key={project.id}
                      onClick={() => {
                        onProjectChange(project);
                        setShowProjectSelector(false);
                      }}
                      className={`w-full flex items-center space-x-3 p-3 rounded-md transition-colors ${
                        project.id === currentProject.id
                          ? 'bg-blue-50 text-blue-700'
                          : 'hover:bg-gray-50'
                      }`}
                      data-testid={`project-option-${project.id}`}
                    >
                      <FolderOpen className="w-4 h-4" />
                      <div className="flex-1 text-left">
                        <div className="font-medium truncate">{project.name}</div>
                        <div className="text-sm text-gray-500">{project.projectType}</div>
                      </div>
                      <span className={`text-xs px-2 py-1 rounded-full font-medium ${
                        getProjectRAGStatus(project) === 'Green' ? 'bg-green-100 text-green-700' :
                        getProjectRAGStatus(project) === 'Amber' ? 'bg-amber-100 text-amber-700' :
                        'bg-red-100 text-red-700'
                      }`}>
                        {getProjectRAGStatus(project)}
                      </span>
                    </button>
                  ))
                ) : (
                  <div className="p-4 text-center text-gray-500 text-sm">
                    No projects in {currentProgram?.name}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Work Surfaces Navigation */}
      <div className="p-6 border-b border-gray-200">
        <h3 className="text-sm font-medium text-gray-900 mb-3">Work Surfaces</h3>
        <nav className="space-y-1">
          {WORK_SURFACES.map((surface) => (
            <button
              key={surface.id}
              onClick={() => onSurfaceChange(surface.id)}
              className={`w-full flex items-center space-x-3 p-3 rounded-lg transition-colors ${
                selectedSurface === surface.id
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
              data-testid={`surface-${surface.id}`}
            >
              <span className="text-lg">{surface.icon}</span>
              <div className="flex-1 text-left">
                <div className="font-medium">{surface.label}</div>
                <div className="text-xs text-gray-500">{surface.description}</div>
              </div>
            </button>
          ))}
        </nav>
      </div>

      {/* Filters */}
      <div className="flex-1 p-6">
        <div className="flex items-center space-x-2 mb-3">
          <Filter className="w-4 h-4 text-gray-500" />
          <h3 className="text-sm font-medium text-gray-900">Filters</h3>
        </div>

        <div className="space-y-4">
          <div>
            <label className="block text-xs font-medium text-gray-700 mb-1">RAG Status</label>
            <select
              value={filters.ragStatus}
              onChange={(e) => setFilters(prev => ({ ...prev, ragStatus: e.target.value }))}
              className="w-full text-sm border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              data-testid="filter-rag-status"
            >
              {RAG_STATUS_OPTIONS.map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-xs font-medium text-gray-700 mb-1">Status</label>
            <select
              value={filters.status}
              onChange={(e) => setFilters(prev => ({ ...prev, status: e.target.value }))}
              className="w-full text-sm border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              data-testid="filter-status"
            >
              {STATUS_OPTIONS.map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-xs font-medium text-gray-700 mb-1">Owner</label>
            <select
              value={filters.owner}
              onChange={(e) => setFilters(prev => ({ ...prev, owner: e.target.value }))}
              className="w-full text-sm border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              data-testid="filter-owner"
            >
              {uniqueOwners.map(owner => (
                <option key={owner} value={owner}>{owner}</option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="p-6 border-t border-gray-200">
        <button className="w-full flex items-center space-x-3 p-3 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors">
          <Settings className="w-4 h-4" />
          <span className="text-sm font-medium">Settings</span>
        </button>
      </div>
    </div>
  );
};

